import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor for adding auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor for handling errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// API endpoints
export const forensicAPI = {
  // Dashboard
  getDashboardStats: () => api.get('/dashboard/stats/'),
  getRecentCases: () => api.get('/cases/recent/'),

  // Cases
  getCases: (params) => api.get('/cases/', { params }),
  getCase: (id) => api.get(`/cases/${id}/`),
  createCase: (data) => api.post('/cases/', data),
  updateCase: (id, data) => api.put(`/cases/${id}/`, data),
  deleteCase: (id) => api.delete(`/cases/${id}/`),

  // Artifacts
  getArtifacts: (caseId, params) => api.get(`/cases/${caseId}/artifacts/`, { params }),
  getArtifactDetails: (id) => api.get(`/artifacts/${id}/`),
  searchArtifacts: (query) => api.post('/artifacts/search/', query),

  // Browser artifacts
  getBrowserHistory: (caseId) => api.get(`/cases/${caseId}/browser-history/`),
  getBrowserDownloads: (caseId) => api.get(`/cases/${caseId}/browser-downloads/`),

  // Registry artifacts
  getRegistryData: (caseId) => api.get(`/cases/${caseId}/registry/`),
  getUSBDevices: (caseId) => api.get(`/cases/${caseId}/usb-devices/`),

  // File system
  getFileSystem: (caseId) => api.get(`/cases/${caseId}/filesystem/`),
  getDeletedFiles: (caseId) => api.get(`/cases/${caseId}/deleted-files/`),

  // Timeline
  getTimeline: (caseId, params) => api.get(`/cases/${caseId}/timeline/`, { params }),

  // Reports
  generateReport: (caseId, format) => api.post(`/cases/${caseId}/report/`, { format }),
  getReports: (caseId) => api.get(`/cases/${caseId}/reports/`),

  // Analytics
  getAnomalies: (caseId) => api.get(`/cases/${caseId}/anomalies/`),
  getStatistics: (caseId) => api.get(`/cases/${caseId}/statistics/`),

  // Export
  exportData: (caseId, format) => api.get(`/cases/${caseId}/export/`, {
    params: { format },
    responseType: 'blob',
  }),
};

export default api;
