sourcesContent:
// Color palette extracted from the JPEG image
// Warm earth tones with forensic investigation theme

export const colors = {
  // Primary colors from image
  primary: {
    main: '#8B6F47',      // Warm brown from image
    light: '#A68A64',     // Lighter brown
    dark: '#6B5437',      // Darker brown
    contrastText: '#FFFFFF',
  },

  // Secondary colors
  secondary: {
    main: '#C4A57B',      // Sandy beige from image
    light: '#D4B88F',     // Light sand
    dark: '#9D8461',      // Dark sand
    contrastText: '#2C2416',
  },

  // Accent colors from image highlights
  accent: {
    warm: '#D4A574',      // Warm highlight
    cool: '#8B9DAF',      // Cool blue-grey
    alert: '#C17B5C',     // Reddish brown for alerts
    success: '#7A9B76',   // Muted green
  },

  // Background colors
  background: {
    default: '#F5F1EB',   // Light cream
    paper: '#FFFFFF',
    dark: '#2C2416',      // Dark brown from shadows
    darker: '#1A1410',    // Almost black
    card: '#FAF8F5',      // Slightly warm white
  },

  // Text colors
  text: {
    primary: '#2C2416',   // Dark brown
    secondary: '#6B5437', // Medium brown
    disabled: '#A68A64',  // Light brown
    hint: '#C4A57B',      // Sandy
  },

  // Status colors
  status: {
    critical: '#B85450',  // Muted red
    warning: '#D4A574',   // Warm amber
    info: '#8B9DAF',      // Blue-grey
    success: '#7A9B76',   // Muted green
  },

  // Chart colors (from image palette)
  chart: [
    '#8B6F47',
    '#C4A57B',
    '#D4A574',
    '#8B9DAF',
    '#C17B5C',
    '#7A9B76',
    '#A68A64',
    '#9D8461',
  ],

  // Gradient combinations
  gradients: {
    primary: 'linear-gradient(135deg, #8B6F47 0%, #C4A57B 100%)',
    warm: 'linear-gradient(135deg, #D4A574 0%, #C17B5C 100%)',
    cool: 'linear-gradient(135deg, #8B9DAF 0%, #6B7A8F 100%)',
    dark: 'linear-gradient(135deg, #2C2416 0%, #1A1410 100%)',
  },
};

export default colors;
